mapper.select:
 
 	select s_id , s_name , s_email, s_address1, s_address2, s_address3, s_joindate, s_phone
 	from (
				select	s_id, s_name, s_email,s_address1, s_address2, s_address3 ,s_joindate,s_phone,
						ROW_NUMBER() OVER(ORDER BY s_name DESC) AS rNum
				FROM	Seller
				WHERE	1=1
 

	
		) brd
		WHERE	rNum 	BETWEEN 1 + (#{perPageNum} * (#{page}-1) )
						AND		#{perPageNum}*#{page}
		ORDER BY s_name DESC

select s_id , s_name , s_email, s_address1, s_address2, s_address3, s_joindate, s_phone
from Seller
where s_id = #{s_id}
